// E[return] = Σ p(slot) * multiplier(slot)
// p(slot) = C(rows, slot) / 2^rows  (binomial distribution, P(right)=0.5)
for (const risk of ["low", "medium", "high"]) {
    for (let rows = 8; rows <= 16; rows++) {
        let expectedReturn = 0;
        let totalProb = 0;
        for (let slot = 0; slot <= rows; slot++) {
            let binom = 1;
            const k = Math.min(slot, rows - slot);
            for (let j = 0; j < k; j++) {
                binom = binom * (rows - j) / (j + 1);
            }
            binom = Math.round(binom);
            const prob = binom / Math.pow(2, rows);
            totalProb += prob;
            expectedReturn += prob * PlinkoGameProfiles_1.PlinkoGameProfiles.getMultiplier(risk, rows, slot);
        }
        (0, chai_1.expect)(Math.abs(totalProb - 1.0)).to.be.below(1e-9, `${risk}/${rows}: probabilities sum to ${totalProb}`);
        (0, chai_1.expect)(expectedReturn).to.be.greaterThanOrEqual(0.985, `${risk}/${rows}: RTP=${(expectedReturn * 100).toFixed(2)}% too low`);
        (0, chai_1.expect)(expectedReturn).to.be.below(1.01, `${risk}/${rows}: RTP=${(expectedReturn * 100).toFixed(2)}% too high`);
    }
}

